import axios from 'axios';
import rateLimit from "axios-rate-limit"

const Base_Url = 'https://youtube-v31.p.rapidapi.com';
const options = {
  params: {
    part: 'snippet,id',
    maxResults: '50',
  },
  headers: {
    'X-RapidAPI-Key': 'ec8981f2b0msh94681292e269e2cp18f31bjsndfdc48e83c2b',
    'X-RapidAPI-Host': 'youtube-v31.p.rapidapi.com'
  }
};

// Apply rate limiting with a maximum of 5 requests per second
const http = rateLimit(axios.create(), { maxRequests: 1, perMilliseconds: 1000 });

export const FetchFromApi = async (url) => {
  try {
    const response = await http.get(`${Base_Url}/${url}`, options);
    return response.data;
  } catch (error) {
    console.error(error);
    throw new Error('Failed to fetch data from the API');
  }
};
